function g = mdm_gwf_read(gwf_fn)% function g = mdm_gwf_read(gwf_fn)%% Read a gradient waveformif (~exist(gwf_fn, 'file')), error('Did not find: %s', gwf_fn); endfid = fopen(gwf_fn);t = fgetl(fid); % number of elementsg = [];while (1)    if (feof(fid)), break; end    t = fgetl(fid);    g(:,end+1) = str2num(t);end% output in n x 3 formatg = g';fclose(fid);